SELECT id, problem_id, user_id, title, language, status
FROM solutions


        AND problem_id = #{problemId}


        AND user_id = #{userId}


        AND status = #{status}


        AND (
        content LIKE CONCAT('%', #{keyword}, '%') ESCAPE '\\'
        OR title LIKE CONCAT('%', #{keyword}, '%') ESCAPE '\\'
        OR language LIKE CONCAT('%', #{keyword}, '%') ESCAPE '\\'
        )


ORDER BY id DESC

SELECT views
FROM solutions
WHERE id = #{id}
